#include<iostream>
#include<cmath>
#include<algorithm>
#include <cassert>
#include <stdio.h>
#include<string>
using namespace std;
int main() 
{
	int N=0, a[10000],b[10000],c[10000],d[10000],count=0,nc=0;

	cin >> N;
	for (int i = 0; i < N; i++) {
		cin >> a[i];
		
	}
	sort(a, a + N);

	for (int j = 0; j < N; j++) {
		if (a[j] % 2 == 1) {
			b[count] = a[j];
				count++;
		}
		else {
			
			c[nc] = a[j];
			nc++;
		}
		
	}
	sort(b, b +count );
	sort(c, c + nc);
	for (int k = 0; k < count; k++) {
		
			d[k] = b[k];
		
	}
	for (int l = 0; l < nc; l++) {
		
			d[l + count] = c[l];
		
	}
	
	
	for (int m = 0; m < N-1; m++) {
		cout << d[m];
		cout << " ";
	}
	cout << d[N - 1] << endl;
	return 0;
}
