#include "iostream"
#include "string"
#include "algorithm"
#include "vector"

using namespace std;

int main() {
	int a[100][100];
	int h, w, x, th, tw;
	cin >> h;
	cin >> w;
	cin >> x;
	int tmp = 1;
	for(int k=0;k<h;k++){
		for (int i = 0; i < w; i++) {
			a[k][i] = tmp;
			if (tmp == x) {
				th = i;
				tw = k;
			}
			tmp++;
		}
	}

	int b[300][300];

	for (int j = 0; j < 3; j++) {
		for (int l = 0; l < 3; l++) {
			for (int k = 0; k < h; k++) {
				for (int i = 0; i < w; i++) {
					b[k + h * j][i + w * l] = a[k][i];
				}
			}
		}
	}

	int ah, aw;
	ah = th + h;
	aw = tw + w;
	//cout << b[aw][ah] << endl;
	vector<int> ans;
	for (int i = -1; i < 2; i++) {
		for (int k = -1; k < 2; k++) {
			if (i == 0 && k == 0);
			else
			{
				ans.push_back(b[aw - i][ah -k]);
			}
		}
	}

	sort(ans.begin(), ans.end());

	for (int i = 0; i < ans.size(); i++) {
		cout << ans[i];
		if (i != ans.size() - 1) cout << " ";
	}
	cout << endl;


	/*
	for (int k = 0; k < h * 3; k++) {
		for (int i = 0; i < w * 3; i++) {
			cout << b[k][i];
		}
		cout << endl;
	}
	*/
	

	return 0;
}
