#include<iostream>
#include<string>
#include<vector>
#include <stdio.h>
#include <stdlib.h>
#include <time.h>
#include <algorithm>


int main() {
	std::vector<int>result;
	int H, W, X;
	std::cin >> H >> W >> X;
	X--;
	if (X == 0)result.push_back(H*W);
	else if (X < H)result.push_back(H * W - (H - X));
	else if (X%H == 0)result.push_back(X - 1);
	else result.push_back(X - H - 1);

	if (X < H)result.push_back(H * W - (H - X) + 1);
	else result.push_back(X - H);

	if (X == H - 1)result.push_back(H*W - (H - 1));
	else if (X < H)result.push_back(H * W - (H - X) + 2);
	else if (X % H == H - 1)result.push_back(X - (H * 2) + 1);
	else result.push_back(X - H + 1);

	if (X % H == 0)result.push_back(X + H);
	else result.push_back(X - 1);

	if (X % H == H - 1)result.push_back(X - H + 1);
	else result.push_back(X + 2);

	if (X == H * W - H + 1)result.push_back(H);
	else if (X > H * W - H)result.push_back(X % H);
	else if (X % H == 0)result.push_back(X + (H * 2));
	else result.push_back(X + H - 1);

	if (X > H*W - H)result.push_back(X % H + 1);
	else result.push_back(X + H + 1);

	if (X == H * W)result.push_back(1);
	else if (X > H*W - H)result.push_back(X % H + 2);
	else if (X % H == H - 1)result.push_back(X + 1);
	else result.push_back(X + H + 2);

	std::sort(result.begin(), result.end());
	for (int i = 0; i < result.size(); i++) {
		std::cout << result[i];
		if (i != result.size() - 1)std::cout << std::endl;
	}
	std::cout<<std::endl;
	return 0;
}
