#include <iostream>
#include <algorithm>
#include <cmath>
#include <string>
#include <vector>
#include <queue>
typedef unsigned long long ULL;

int main() {
	std::string str;
	std::cin >> str;
	int m = 0;
	int g = 0;
	int r = 0;

	for (int i = 0; i < str.size(); ++i) {
		if (str[i] == 'M') {
			m += 1;
		}
		if (str[i] == 'G') {
			g += 1;
		}
		if (str[i] == 'R') {
			r += 1;
		}
	}
	if (m >= 1 && g >= 1 && r >= 1) {
		std::cout << "YES" << std::endl;
	}
	else {
		std::cout << "NO" << std::endl;
	}
	return 0;
}
