#include<iostream>
int main() {
	int long n,k=0,l=0,m=0, a[100],b[100],c[100];
	std::cin >> n;
	for (int i = 0; i < n; i++) {
		std::cin >> a[i];
		if (a[i] % 2 == 1) {
			b[k] = a[i];
			k++;
		}
		else {
			c[l] = a[i];
			l++;
		}
	}
	for (int i = 0; i < k-1; i++) {
		for (int j = 0; j < k - 1; j++) {
			if (b[j] > b[j + 1]){
				m = b[j + 1];
			b[j + 1] = b[j];
			b[j] = m;
			}
		}
	}
	for (int i = 0; i < l-1; i++) {
		for (int j = 0; j < l-1; j++) {
			if (c[j] > c[j + 1]) {
				m = c[j + 1];
				c[j + 1] = c[j];
				c[j] = m;
			}
		}
	}
	if (k == 0) {
		for (int i = 0; i < l - 1; i++) {
			std::cout << c[i] << " ";
		}
		std::cout << c[l - 1] << std::endl;
	}
	else {
		for (int i = 0; i < k - 1; i++) {
			std::cout << b[i] << " ";
		}
		if (l == 0)
			std::cout << b[k - 1] << std::endl;
		else {
			std::cout << b[k - 1] << " ";
			for (int i = 0; i < l - 1; i++) {
				std::cout << c[i] << " ";
			}
			std::cout << c[l - 1] << std::endl;
		}
	}
	return 0;
}
