#include<iostream>
#include<string>

bool m,g,r;

int mcount,gcount;

std::string str;

int main(){
    
    std::cin>>str;
    
    for(auto&& x:str){
        
        if(x=='M'){
            m=true;
            goto f;
        }
        
        if(x=='G'){
            if(m==true)
                g=true;
            goto f;
        }
        
        if(x=='R'){
            if(g==true)
                r=true;
            goto f;
        }
        
        if(m&&!g){
            mcount++;
        }
        
        if(m&&g){
            gcount++;
        }
        
        if(m&&!g)
        if(mcount==1)
            if(x!='A')
                m=false;
        
        if(m&&g){
            if(gcount==1)
                if(x!='A')
                    g=false;
            if(gcount==2)
                if(x!='E')
                    g=false;
        }
        
    f:;
        
    }
    
    if(r==true)
        std::cout<<"YES"<<std::endl;
    
    else
        std::cout<<"NO"<<std::endl;
    
    return 0;
    
}
