#include<iostream>
#include<string>
int main() {
	int h=0, k=0;
	std::string s;
	std::cin >> s;
	for (int i = 0; i < s.size(); i++) {
		for (int j = i + 1; j < s.size(); j++) {
			if (s[i] == 'r') {

				if (s[j] == 'u') {
					k += 1;
				}
				if (s[j] == 'd') {
					k -= 1;
				}
			}
				if (s[i] == 'l') {
					if (s[j] == 'u') {
						h += 1;
					}
					if (s[j] == 'd') {
						h -= 1;
					}
				}
			if (h < 0) {
				h = 23;
			}
			if (h == 24) {
				h = 0;
			}
			if (k < 0) {
				k = 23;
			}
			if (k == 24) {
				k = 0;
			}
		}
	}
	std::cout << h << " " << k;
}
