#include <algorithm>
#include <iostream>
#include <string>
#define MAX 100

int main() {
	std::string num;
	std::cin >> num;
	int size = long(num.size());
	int sizek = size / 2;
	int sizea = size % 2;
	if (sizea == 1) {
		sizek++;
	}
	long nums[MAX];
	for (long i = size; i >= 0; i--) {
		long nn = long(num[i]);
		nums[i] = nn;
	}
	//
	std::string num2;
	std::cin >> num2;
	long size2 = long(num2.size());
	long nums2[MAX];
	for (long i = size; i >= 0; i--) {
		long nn = long(num2[i]);
		nums2[i] = nn;
	}
	long numsadd[MAX];
	for (long i = 0; i < 100; i++) {
		numsadd[i] = nums[i] + nums2[i];
		if (numsadd[i] >= 10) {
			numsadd[i] -= 10;
			numsadd[i + 1]++;
		}
	}
	for (long i = 100; i >= 0; i--) {
		if (numsadd[i] != NULL) {
			printf("%d",numsadd[i]);
		}
	}
	std::cout << std::endl;
	return 0;
}
