#include<iostream>
#include<cstring>
#include<algorithm>
#include<vector>
using namespace std;
int main(){
    int n;
    cin>>n;
    vector<int>a(n);
    vector<int>b(0);
    vector<int>c(0);
   // vector<int>d(n);
    for(int i=0;i<n;i++){
        cin>>a[i];
    }
    for(int i=0;i<n;i++){
        if(a[i]%2==1){
            b.push_back(a[i]);
        }else{
            c.push_back(a[i]);
        }
    }
    sort(b.begin(),b.end());
    sort(c.begin(),c.end());
    for(int i=0;i<b.size();i++){
        cout<<b[i]<<" ";
    }
    for(int i=0;i<c.size();i++){
        cout<<c[i];
        if(i<c.size()-1){
            cout<<" ";
        }
    }
    cout<<endl;
}
