using namespace std;
using ll = long long;
typedef unsigned long long ull;
typedef pair<ll, ll> P;
 
const ll MOD = 1e9 + 7;
const ll INF = 1 << 30;
const ll INF2 = 9e18;
const double INF3 = 9e14;
const int dx[4] = { 1,0,-1,0 }, dy[4] = { 0,1,0,-1 };
const int tx[8] = { -1,0,1,-1,1,-1,0,1 }, ty[8] = { -1,-1,-1,0,0,1,1,1 };
#define ALL(x) (x).begin(),(x).end()
#define pb push_back
#define fr first
#define sc second
 
 
 
int main() {
	ll t;
	cin >> t;
	vector<P>fib(100010);
	fib[0] = P(1, 0), fib[1] = P(0, 1);
	for (int i = 2;i < 100005;i++) {
		fib[i].fr = (fib[i - 1].fr + fib[i - 2].fr) % MOD;
		fib[i].sc = (fib[i - 1].sc + fib[i - 2].sc) % MOD;
	}
	for (int z = 0;z < t;z++) {
		ll n, m, a[101000], b[100010], ans = 0;
		cin >> m >> n;
		for (int i = 0;i < m;i++) {
			cin >> a[i];
			ans = (ans + (((a[i] * fib[n-1].fr) % MOD)*m)) % MOD;
		}
		for (int i = 0;i < m;i++) {
			cin >> b[i];
			ans = (ans + (((b[i] * fib[n-1].sc) % MOD)*m)) % MOD;
		}
		cout << ans << endl;
	}
	return 0;
}
