#include<iostream>
#include<vector>
#include<algorithm>
using namespace std;
struct MIKAN{
	int a, b;
};

void knapsack(vector<MIKAN> mikan, int idx, int weight, int price, int &pricemax, int max){
	if (mikan.size()-1<idx||max < weight + mikan[idx].a )return;
	else {
		weight += mikan[idx].a;
		price += mikan[idx].b;
		if (pricemax < price)pricemax = price ;
		knapsack(mikan, 2 * idx + 1, weight, price, pricemax, max);
		knapsack(mikan, 2 * idx + 2, weight, price, pricemax, max);
	}
}
int main(){
	int num, max;
	vector<MIKAN> mikan;
	cin >> num;
	cin >> max;
	for (int i=0; i < num; i++){
		int a, b;
		cin >> a;
		cin >> b;
		MIKAN m;
		m.a = a; m.b = b;
		mikan.push_back(m);
	}
	sort(mikan.begin(), mikan.end(),
		[](const MIKAN& a, const MIKAN& b){return a.b > b.b; });
	int pricemax = 0;
	knapsack(mikan, 0, 0, 0, pricemax, max);
	cout << pricemax << endl;
	cin >> num;
}
