#include <iostream>
#include <algorithm>
#include <string>

int sh,sm,th,tm;

int main() {

    char ch;

    std::string s,t;

    std::cin>>s>>t;

    if(s[0]=='0')
        sh=atoi(s.c_str()+1);
    else
        sh=std::stoi(s.substr(0,2));
    if(s[2]=='0')
        sm=atoi(s.c_str()+3);
    else
        sm=std::stoi(s.substr(2));
    if(t[0]=='0')
        th=atoi(t.c_str()+1);
    else
        th=std::stoi(t.substr(0,2));
    if(t[2]=='0')
        tm=atoi(t.c_str()+3);
    else
        tm=std::stoi(t.substr(2));

    int right=th+tm,left=(th-sm+24)%24+(tm-sm+60)%60;

    std::cout<<std::max(left,right)<<std::endl;

    return 0;
}
