#include <iostream>
#include <algorithm>
#include <string>

int sh,sm,th,tm;

int main() {

    char ch;

    std::string s,t;

    std::cin>>s>>t;

    
    sh=std::stoi(s.substr(0,2));
    sm=std::stoi(s.substr(3));
    th=std::stoi(t.substr(0,2));
    tm=std::stoi(t.substr(3));

    int right=th+tm,left=(th-sh+24)%24+(tm-sm+60)%60;

    std::cout<<std::min(left,right)<<std::endl;

    return 0;
}
