#include <iostream>
#include <set>

std::set<double> st;

int main(){
  int n;
  std::cin>>n;
  double x[1001], y[1001];
  for(int i=0;i<n;++i){
    std::cin>>x[i]>>y[i];
  }
  double g1,g2,g3;
  double dx1=x[0]-x[1], dx2=x[1]-x[2], dx3 = x[2] - x[0];
  double dy1=y[0]-y[1], dy2=y[1]-y[2], dy3 = y[2] - y[0];
  if(dx1!=0){
    g1 = dy1/dx1;
  }else{
    g1=1e17;
  }

  if(dx2!=0){
    g2 = dy2/dx2;
  }else{
    g2=1e17;
  }

  if(dx3!=0){
    g3 = dy3/dx3;
  }else{
    g3 = 1e17;
  }

  st.emplace(g1);
  st.emplace(g2);
  st.emplace(g3);

  for(auto&& d: st){
    std::set<double> st2;
    for(int i=0;i<n;++i){
      
      st2.emplace(y[i]-d*x[i]);
    }
    if(st2.size()==2){
      std::cout<<"Yes"<<std::endl;
      return 0;
    }
  }
  std::cout<<"No"<<std::endl;
}
