#include "iostream"
#include "string"
#include "algorithm"
#include "vector"
#include "map"

using namespace std;

int main() {
	int n, m;
	cin >> n >> m;
	int ans = 0;
	ans += n + m;
	if (n - m >= 0) {
		ans += n - m;
	}
	else ans += m - n;
	int tmp = m / n;
	if (tmp < 0) tmp *= -1;
	ans += tmp;
	ans += m * n;
	ans += n % m;
	cout << ans << endl;

	return 0;
}
