#include<iostream>
int main()
{
	int N, A, B, C, D;
	std::cin >> N >> A >> B >> C >> D;
	int X, Y;
	if (N%A=0) {
		int X = N / A * B;
	}
	else {
		int X = (N / A + 1)*B;
	}
	if (N%C=0) {
		int Y = N / C * D;
	}
	else {
		int Y = (N / C + 1)*D;
	}
	if (X > Y) {
		std::cout << Y << std::endl;
	}
	else {
		std::cout << X << std::endl;
	}
}
