const int dx[4] = { 1,0,-1,0 }, dy[4] = { 0,1,0,-1 };

#define ALL(x) (x).begin(),(x).end()
#define ALLR(x) (x).rbegin(),(x).rend()
#define pb push_back
#define eb emplace_back
#define fr first
#define sc second

ll n, p, a[25];

int main() {
	cin >> n >> p;
	for (int i = 0;i < n;i++)cin >> a[i];
	for (int i = 0;i < (1 << n);i++) {
		ll l = 1, r = 1;
		for (int j = 0;j < n;j++) {
			if (i&(1 << j))l *= a[j], l %= p;
			else r *= a[j], r %= p;
		}
		if (r%p == l % p) {
			cout << "Yes" << endl;
			return 0;
		}
	}
	cout << "No" << endl;
	return 0;
}
