using ll = long long;
using ull = unsigned long long;
using namespace std;

const ll INF = 1000000000000;
const ll MOD = 1000000007;

ll n, m, ans = 0, A[101010];
string s;
int sa(int n, int m) {
	if (n >= 0) {
		if (m >= 0)
			return max(n, m) - min(n, m);
		else
			return n + abs(m);
	}
	else {
		if (m >= 0)
			return m - n;
		else
			return abs(min(n, m)) - abs(max(n, m));
	}
}
int main() {
	cin >> n >> m;
	if (n > m)
		ans += (n + m) + sa(n, m) + (n * m) + (n / m) + (n % m);
	else
		ans += (n + m) + sa(n, m) + (n * m) + (m / n) + (m % n);
	cout << ans << endl;
	return 0;
}
