#include <iostream>
#include <algorithm>
#include <string>
#include <vector>

int main() {
	int n, m;
	std::cin >> n >> m;
	int a = n + m;
	int b;
	int c;
	int d = n * m;
	int e = n % m;
	if (n > m) {
		b = n - m;
		c = n / m;
	}
	else {
		b = m - n;
		c = m / n;
	}
	int g = a + b + c + d + e;
	std::cout << g << std::endl;
}
