#include <iostream>
#include <string>
int main(){
	int n,m;
	std::cin>>n>>m;
	if(n>m){
		std::cout<<(n+m)+(n-m)+(n/m)+(n*m)+(n%m)<<std::endl;
	}
	if(m>n){
		std::cout<<(m+n)+(m-n)+(m/n)+(n*m)+(m%n)<<std::endl;
	}
	if(m==n){
		std::cout<<(m+n)+1+(n*m)<<std::endl;
	}
	return 0;
}
