#include "iostream"
#include "string"
#include "algorithm"
#include "vector"
#include "map"

using namespace std;

int main() {
	int n, m;
	cin >> n >> m;
	int a, b, c, d ,e;
	a = n + m;
	if (n - m >= 0) {
		b = n - m;
	}
	else b = m - n;
	c = n * m;
	d = n / m;
	e = n % m;
	int ans = a + b + c + d + e;
	cout << ans << endl;

	return 0;
}
