#include<iostream>
#include<string>
#include<random>
#include<ctime>
#include<cmath>
int main() {
	int n,ans=0;
	std::cin >> n;
	while (true) {
		if (n == 1)break;
		else if (n % 2 == 0) {
			ans++;
			n /= 2;
		}
		else {
			ans++;
			n = 3 * n + 1;
		}
	}
	std::cout << ans << "\n";
}
