#include<iostream>
int main() {
	int n = 0, m = 0, a = 0, b = 0, c = 0, d = 0, e = 0;
	std::cin >> n, m;
	a = n + m;
	if (n <= m) {
		c = m / n;
		e = m%n;
		b = m - n;
	}else {
		c = n / m;
		e = n%m;
		b = n - m;
	}
	d = n*m;
	std::cout << a + b + c + d + e << std::endl;
}
