#include<iostream>
#include<algorithm>
using namespace std;

int main()
{
	int n, ans = 0, pans = 0;
	cin >> n;
	for (size_t i = 0; i < n; i++)
	{
		int input;
		cin >> input;
		if (input) pans++;
		else
		{
			ans = max(ans, pans);
			pans = 0;
		}
	}
	cout << max(ans, pans) + 1 << endl;
}
