#include "iostream"
#include "string"
#include "algorithm"
#include "vector"

using namespace std;

int main() {
	string s;
	cin >> s;
	vector<char> ans;
	int count = 0;
	for (int i = 0; i < s.size(); i++) {
		if (i == 0 || i == s.size() - 1) {
			ans.push_back(s[i]);
		}
		else count++;

	}
	cout << ans[0] << count << ans[1] << endl;

	return 0;
}
