#include <iostream>
#include <algorithm>
#include <string>
#include <vector>

int main() {
	long a;
	std::cin >> a;
	int b = 0;
	while (true) {
		if (a == 1) {
			break;
		}
		else if (a % 2 == 0) {
			a = a / 2;
		}
		else if (a % 2 == 1) {
			a = a * 3 + 1;
		}
		b++;
	}
	std::cout << b << std::endl;
}
