#include<iostream>
#include<algorithm>
#define rep(i,rept) for (ll i = 0; i < rept; i++)
	typedef long long ll;

	int main() {
		int n = 0, m = 0;
		int sa = 0, syo = 0, amari = 0;
		std::cin >> n >> m;
		sa = std::max(n, m) - std::min(n, m);
		syo = std::max(n, m) / std::min(n, m);
		amari = std::max(n, m) / std::min(n, m);
		std::cout << (n + m) + sa + syo + (n*m) + amari << std::endl;
		return 0;
	}
