#include<iostream>
#include<vector>
#include<cstring>
#include<algorithm>
using namespace std;
int main(){
    int n,m;
    cin>>n>>m;
    int a=n+m;
    int b;
    int d;
    if(n>=m){
        b=n-m;
        d=n/m;
    }else{
        b=(n-m)*-1;
        d=(n/m)*-1;
    }
    int c=n*m;
    int e=n%m;
    cout<<a+b+c+d+e<<endl;
    return 0;
}
