#include<iostream>
#include<string>
#include<cmath>

std::string str;

int res;

int hour1,hour2,minute1,minute2;

int main(){
    
    std::cin>>str;
    
    hour1=(str[0]-'0')*10+str[1]-'0';
    
    minute1=(str[3]-'0')*10+str[4]-'0';
    
    std::cin>>str;
    
    hour2=(str[0]-'0')*10+str[1]-'0';
    
    minute2=(str[3]-'0')*10+str[4]-'0';
    
    hour2+=24;
    
    minute2+=60;
    
    res=std::min((hour2-hour1)%24+(minute2-minute1)%60,hour2%24+minute2%60);
    
    std::cout<<res<<std::endl;
    
    return 0;
}
