#include<iostream>
#include<algorithm>
#define rep(i,rept) for (ll i = 0; i < rept; i++)
	typedef long long ll;

	int main() {
			ll n = 0, m = 0;
			ll sa = 0, syo = 0, amari = 0;
			ll tasi = 0, kake = 0;
			std::cin >> n >> m;
			sa = std::abs(n - m);
			syo = std::max(n, m) / std::min(n, m);
			tasi = std::max(n, m) + std::min(n, m);
			kake = std::max(n, m) * std::min(n, m);
			amari = std::max(n, m) % std::min(n, m);
			std::cout << tasi + sa + syo + kake + amari  << std::endl;
			return 0;
	}
