#include<iostream>
using namespace std;
int main()
{
	int n, m;
	cin >> n >> m;
	unsigned long int a, b, c, d, e;
	a = n + m;
	if (n > m) {
	b = n - m;
	c = n / m;
	}
	else if (n < m) { 
	b = m - n; 
	c = m / n;
	}
	d = n * m;
	e = n % m;
	cout << a + b + c + d + e << endl;
}
