	int imahun;
	int atozi;
	int atohun;
	char s;
	std::cin >>imazi >> s >> imahun;
	std::cin >> atozi >> s >> atohun;
	int ans = atozi + atohun;
	if (atozi < imazi) {
		atozi += 24;
	}
	if (atohun < imahun) {
		atozi += 60;
	}
	if (ans > atozi - imazi + atohun - imahun) {
		ans = atozi - imazi + atohun - imahun;
	}
	std::cout << ans << std::endl;
}
