#include<iostream>
#include<algorithm>
#define rep(i,rept) for (ll i = 0; i < rept; i++)
	typedef long long ll;

	int main() {
			ll n = 0, m = 0;
			ll sa = 0;
			std::cin >> n >> m;
			sa = std::abs(n - m);
			std::cout << (n+m) + sa + (n/m) + (n*m) + (n%m) << std::endl;
			return 0;
	}
