#include <iostream>
	int main() {
		int n, a, b, c, d;
		std::cin >> n >> a >> b >> c >> d;
		if (n%a == 0) {
			if (n%c == 0) {
				if (n / a * b <= n / c * d) {
					std::cout << n / a * b << std::endl;
				}
				else {
					std::cout << n / c * d << std::endl;
				}
			}
		}
		else {
			if (n%c == 0) {
				std::cout << n / c * d << std::endl;
			}
			else if (n / c * d <= n / a * b) {
			std::cout << n / c * d << std::endl;
			}
			else {
				std::cout << n / a * b << std::endl;
			}
		}
		std::cout << std::endl;
		return 0;
	}
