#include <iostream>
#include <vector>
#include <string>
using namespace std;

int main() {
	long long a;
	cin >> a;
	int n = 0;

	while (1) {
		if (a == 1) {
			break;
		}
		else if (a % 2 == 0) {
			a = a / 2;
			n++;
		}
		else {
			a = a * 3 + 1;
			n++;
		}
	}
	cout << n << endl;
}
