#include <iostream>
#include <math.h>
using namespace std;

int Eratosthenes(int g, int arr[]) {
	int kazu = 0;
	for (int i = 0; i < g; i++) {
		arr[i] = 1;
	}
	for (int i = 2; i < sqrt(g); i++) {
		if (arr[i]) {
			for (int j = 0; i * (j + 2) < g; j++) {
				arr[i *(j + 2)] = 0;
			}
		}
	}

	for (int i = 2; i < g; i++) {
		if (arr[i]) {
			kazu++;
		}
	}
	return kazu;
}


int main() {
	int g;
	std::cin >> g;
	int arr[100000];
	int kazu = Eratosthenes(g,arr);
	std::cout << kazu << std::endl;
}
