#include<iostream>
#include<algorithm>
#define rep(i,rept) for (ll i = 0; i < rept; i++)
	typedef long long ll;

	int main() {
		ll n = 0;
		ll count = 0;
		std::cin >> n;
		while (n != 1) {
			if (n % 2 == 0) {
				n = n / 2;
			}
			else {
				n = n * 3 + 1;
			}
			count++;
		}
		std::cout << count << std::endl;
	}
