#include "iostream"
#include "string"
#include "algorithm"
#include "vector"

using namespace std;

int main() {
	string a, b;
	cin >> a >> b;
	string tmp = "aa";
	int ah, am, bh, bm;
	tmp[0] = a[0];
	tmp[1] = a[1];
	ah = stoi(tmp);
	tmp[0] = a[3];
	tmp[1] = a[4];
	am = stoi(tmp);
	tmp[0] = b[0];
	tmp[1] = b[1];
	bh = stoi(tmp);
	tmp[0] = b[3];
	tmp[1] = b[4];
	bm = stoi(tmp);
	
	int r = 0, n = 0;;
	r += bh + bm;

	if (bm < am) bm += 60;
	if (bh < ah) bh += 24;
	n += bh - ah + bm - am;

	if (r < n) cout << r << endl;
	else cout << n << endl;


	return 0;
}
