#include<iostream>

unsigned long long n,res;

int main(){
    
    std::cin>>n;
    
    while(n!=1){
        
        if(n%2)
            n=n*3+1;
        else
            n=n/2;
        
        res++;
        
    }
    
    std::cout<<res<<std::endl;
    
    return 0;
}
