#include <iostream>
int main() {
	int n, m;
	int sa, syou, amari = 0;
	std::cin >> n >> m;
	if (m > n)
		sa = m - n, syou = m / n;
	else if (m < n)
		sa = n - m, syou = n / m, amari = n % m;
	std::cout << (n + m) + sa + syou + n * m + amari << std::endl;
	system("pause");
	return 0;
}
