#include <iostream>
#include <algorithm>
#include <string>
#include <vector>
#include <cmath>
#define rep(i,rept) for(long long i = 0; i<rept;i++)
using namespace std;
int main() {
	long long x, y=0;
	cin >> x;
	while (x != 1) {
		if (x % 2 == 0) {
			x = x / 2;
		}
		else {
			x = x * 3 + 1;
		}
		y++;
	}
	cout << y << endl;
}
