#include <iostream>
#include <algorithm>
#include <cmath>
#include <string>
#include <vector>
#include <queue>
typedef unsigned long long ULL;

int main() {
	int n;
	int temp = 0;
	std::cin >> n;

	while (n != 1) {
		if (n % 2 == 0) {
			n /= 2;
			temp += 1;
		}
		else {
			n *= 3;
			n += 1;
			temp += 1;
		}
	}

	std::cout << temp << std::endl;
	return 0;

}
