#include <iostream>
#include <algorithm>
#include <string>
#include <vector>
#include <map>
#include <iomanip>
#include <cmath>
#include <cstdlib>
#include <math.h>
#include <stdlib.h>

int main()
{
	int nowh, nowm, tarh, tarm, pushh, pushm, reset;
	std::string input;
	std::cin >> input;
	nowh = (input[0] - '0') * 10 + (input[1] - '0');
	nowm = (input[3] - '0') * 10 + (input[4] - '0');
	std::cin >> input;
	tarh = (input[0] - '0') * 10 + (input[1] - '0');
	tarm = (input[3] - '0') * 10 + (input[4] - '0');
	pushh = tarh - nowh;
	if(pushh<0) pushh += 24;
	pushm = tarm - nowm;
	if(pushm<0) pushm += 60;
	reset = tarh + tarm + 1;
	std::cout << std::min(pushh + pushm, reset)<< std::endl;
	return 0;
}
