#include<iostream>
#include<vector>
#include<cstring>
#include<algorithm>
using namespace std;
int main(){
    int n;
    cin>>n;
    int sum=0;
    long long a=1000000000000;
    for(int i=0;i<a;i++){
        if(n==1){
            a=a-999999999999;
        }else if(n%2==0){
            n=n/2;
            sum+=1;
        }else{
            n=3*n+1;
            sum+=1;
        }
    }
    cout<<sum<<endl;
}
