int main() {
	long n, m;
	std::cin >> n >> m;
	long a = n + m;
	long b;
	long c;
	long d = n * m;
	long e = n % m;
	if (n >= m) {
		b = n - m;
		c = n / m;
	}
	else {
		b = m - n;
		c = m / n;
	}
	long long g = a + b + c + d + e;
	std::cout << g << std::endl;
}
