const int N = 2000100;
const int mod = (int)pow(10, 9) + 7;
const ll INF = 1LL << 60;
#define rep(i,n) for(ll i=(ll)0;i<(ll)n;++i)
#define ALL(x) x.begin(),x.end()
#define pp pair<ll,ll>
#define ppd pair<double,double>
#define ppp pair<ll,pp>
#define fi first
#define se second
#define pb push_back
#define fix(n) fixed<<setprecision(n)
#define itr vector<ll>::iterator
#define print(s) cout<<s<<endl
#define inp(s) cin>>s
string yn(bool b) { return (b ? "Yes" : "No"); }
string YN(bool b) { return (b ? "YES" : "NO"); }
double f(ppd p, ppd p2) {
	return (p.fi == p2.fi ? -INF : (p.se - p2.se) / (p.fi - p2.fi));
}
double n, d[2] = { INF,INF };
ppd p = { INF,INF };
ppd v[1010];
bool ans = 1,ans2=1;
int main() {
	cin >> n;
	rep(i, n)cin >> v[i].fi >> v[i].se;
	for (int i = 1; i < n; ++i) {
		//cout << i << " " << f(v[0], v[i]) << endl;
		if (i == 1)d[0] = f(v[0], v[1]);
		else {
			if (d[0] != f(v[0], v[i])) {
				if (d[1] == INF) {
					if (p != ppd{ INF,INF })d[1] = f(p, v[i]);
					else p = v[i];
				}
				else if (d[1] != f(p, v[i])) { ans = 0; break; }
			}
		}
	}
	if (d[0] != d[1])ans = 0;
	d[1] = INF;
	d[0] = f(v[0], p);
	p = ppd{ INF,INF };
	for (int i = 1; i < n; ++i) {
		if (d[0] != f(v[0], v[i])) {
			if (d[1] == INF) {
				if (p != ppd{ INF,INF })d[1] = f(p, v[i]);
				else p = v[i];
			}
			else if (d[1] != f(p, v[i])) { ans2 = 0; break; }
		}
	}
	cout << yn((ans||ans2)&&d[1] != INF && d[0] == d[1]) << endl;
	return 0;
}
