#include <iostream>
#include <string>
using namespace std;
int main(){
    string a,b;
    cin>>a>>b;
    int ha,hb,ma,mb,p,pr;
    ha=(a[0]-48)*10+a[1]-48;
    hb=(b[0]-48)*10+b[1]-48;
    ma=(a[3]-48)*10+a[4]-48;
    mb=(b[3]-48)*10+b[4]-48;
    if(ha>hb){
        p=24-ha+hb;
    }
    else{
        p=hb-ha;
    }
    if(ma>mb){
        p=p+60-ma+mb;
    }
    else{
        p=p+mb-ma;
    }
    pr=hb+mb+1;
    if(p>pr){
        cout<<pr<<endl;
    }
    else{
        cout<<p<<endl;
    }
}
