#include <iostream>
#include <algorithm>
#include <string>
#include <vector>
#include <map>
#include <iomanip>
#include <cmath>
#include <cstdlib>
#include <math.h>
#include <stdlib.h>

int main()
{
	int n, time = 0;
	std::cin >> n;
	while(n!=1){
		if(n%2==0){
			n /= 2;
		}else{
			n = 3*n + 1;
		}
		time++;
	}
	std::cout << time << std::endl;
	return 0;
}
