#include<iostream>
#include<algorithm>
#define rep(i,rept) for (ll i = 0; i < rept; i++)
	typedef long long ll;

	int main() {
		ll i = 0, j = 0;
		ll n = 0,ans = 0;
		ll count = 0;
		ll arr[100000] = {};
		std::cin >> n;
		rep(i, n) {
			arr[i] = 1;
		}
		for (int i = 2; i < sqrt(n); i++) {
			if (arr[i]) {
				for (int j = 0; i*(j + 2) < n; j++) {
					arr[i*(j + 2)] = 0;
				}

			}
		}
		for (int i = 2; i < n; i++) {
			if (arr[i]) {
				ans++;
			}
		}
		std::cout << ans << std::endl;

	}
