#include <iostream>
#include <math.h>
using namespace std;

long Eratosthenes(long g, long *arr) {
	long kazu = 0;
	for (long i = 0; i < g; i++) {
		arr[i] = 1;
	}
	for (long i = 2; i < sqrt(g); i++) {
		if (arr[i]) {
			for (long j = 0; i * (j + 2) < g; j++) {
				arr[i *(j + 2)] = 0;
			}
		}
	}

	for (long i = 2; i < g; i++) {
		if (arr[i]) {
			kazu++;
		}
	}
	return kazu;
}


int main() {
	long g;
	std::cin >> g;
	long *arr;
	arr = new long[1000000];
	long kazu = Eratosthenes(g, arr);
	std::cout << kazu << std::endl;
}
